define([
    // Module being tested
    "epi-ecf-ui/command/DetachFromCategory",

    // Dependencies
    "epi/shell/selection"

], function (
    DetachFromCategory,
    Selection
) {

    describe("epi-ecf/command/DetachFromCategory", function () {

        var selection, modelMock, storeMock, currentContext, contentData;
        var command;

        beforeEach(function () {

            currentContext = {
                id: 1
            };

            storeMock = {
                query: function () {
                    return ["relation1", "relation2"];
                }
            };

            modelMock = {
                getCurrentContext: function () {
                    return currentContext;
                }
            };

            selection = new Selection();

            command = new DetachFromCategory({
                selection: selection,
                model: modelMock,
                _store: storeMock,
                _typeIsAssignableFrom: function () {
                    return true;
                }
            });

            contentData = {
                parentLink: 1,
                properties: {
                    isRelatedToCurrentCategory: true
                }
            };

            selection.set("data", [{
                type: "epi.cms.contentdata", data: contentData
            }]);

        });

        afterEach(function () {
            command = null;
            selection = null;
        });


        describe("#_onModelChange", function () {

            describe("when selectionData has been set with property isRelatedToCurrentCategory to true", function () {
                beforeEach(function () {
                    contentData.properties.isRelatedToCurrentCategory = true;
                });

                describe("and currentContext is parent", function () {
                    beforeEach(function () {
                        currentContext.id = 666;
                        contentData.parentLink = currentContext.id;
                        command._onModelChange();
                    });

                    it("detach command should not be executable", function () {
                        expect(command.canExecute).to.be.equal(false);
                    });
                });

                describe("and currentContext is not parent", function () {
                    beforeEach(function () {
                        currentContext.id = 666;
                        contentData.parentLink = 667;
                        command._onModelChange();
                    });

                    it("detach command should be executable", function () {
                        expect(command.canExecute).to.be.equal(true);
                    });
                });
            });

            describe("when selectionData has been set with property isRelatedToCurrentCategory to false", function () {
                beforeEach(function () {
                    contentData.properties.isRelatedToCurrentCategory = false;
                    command._onModelChange();
                });

                it("detach command should not be executable", function () {
                    expect(command.canExecute).to.be.equal(false);
                });
            });

            describe("when isUsedByCollectionEditor has been set to true", function () {
                beforeEach(function () {
                    command.isUsedByCollectionEditor = true;
                });

                describe("and categoryLink has not been set", function () {
                    beforeEach(function () {
                        command.categoryLink = null;
                        command._onModelChange();
                    });

                    it("detach command should not be executable", function () {
                        expect(command.canExecute).to.be.equal(false);
                    });
                });

                describe("and categoryLink has been set same as parentlink", function () {
                    beforeEach(function () {
                        command.categoryLink = 666;
                        contentData.parentLink = command.categoryLink;
                        command._onModelChange();
                    });

                    it("detach command should not be executable", function () {
                        expect(command.canExecute).to.be.equal(false);
                    });
                });

                describe("and categoryLink has been set different from parentlink", function () {
                    beforeEach(function () {
                        command.categoryLink = 666;
                        contentData.parentLink = 667;
                        command._onModelChange();
                    });

                    it("detach command should be executable", function () {
                        expect(command.canExecute).to.be.equal(true);
                    });
                });
            });
        });
    });
});

define([
    // Module being tested
    "epi-ecf-ui/command/PasteCatalogContent",
    "epi-ecf-ui/contentediting/ModelSupport",

    // Dependencies
    "epi/shell/ClipboardManager",
    "epi/shell/selection",

    // framework
    "epi/shell/TypeDescriptorManager"
], function (
    PasteContent,
    ModelSupport,
    ClipboardManager,
    Selection,
    TypeDescriptorManager
) {

        describe("epi-cms/command/PasteCatalogContent", function () {

            var clipboard, selection;
            var command, typeDescriptorManagerMock, isBaseTypeIdentifierSpy;

            beforeEach(function () {
                clipboard = new ClipboardManager({
                });

                selection = new Selection();

                command = new PasteContent({
                    clipboard: clipboard,
                    selection: selection,
                    model: {
                        canPaste: function () {
                            return true;
                        }
                    }
                });

                typeDescriptorManagerMock = {
                    isBaseTypeIdentifier: function () {
                        return true;
                    }
                };

                command.typeDescriptorManager = typeDescriptorManagerMock;

                isBaseTypeIdentifierSpy = sinon.spy(typeDescriptorManagerMock, "isBaseTypeIdentifier");
            });

            afterEach(function () {
                clipboard = null;
                command = null;
                selection = null;
            });

            describe("#_typeIsAssignableFrom", function () {

                var firstArgument = "first";
                var secondArgument = "second";

                beforeEach(function () {
                    command._typeIsAssignableFrom(firstArgument, secondArgument);
                });

                it("should call isBaseTypeIdentifier on TypeDescriptorManager.", function () {
                    expect(isBaseTypeIdentifierSpy).to.be.called;
                });

                it("first argument to isBaseTypeIdentifier should be same as first argument to method.", function () {
                    expect(isBaseTypeIdentifierSpy.firstCall.args[0]).to.be.equal(firstArgument);
                });

                it("second argument to isBaseTypeIdentifier should be same as second argument to method.", function () {
                    expect(isBaseTypeIdentifierSpy.firstCall.args[1]).to.be.equal(secondArgument);
                });
            });

            describe("#_onModelChange", function () {
                describe("when selectionData has not been set", function () {
                    beforeEach(function () {
                        command.canExecute = true;
                        command._onModelChange();
                    });

                    it("should set 'canExecute' to 'false'", function () {
                        expect(command.get("canExecute")).to.be.equal(false);
                    });
                });

                describe("when selectionData has been set", function () {
                    var contentNodeType = "episerver.commerce.catalog.contenttypes.contentnode";
                    beforeEach(function () {
                        command.canExecute = false;
                        selection.set("data", [{ type: "epi.cms.contentdata", data: { typeIdentifier: contentNodeType } }]);
                    });

                    it("should call isBaseTypeIdentifier on TypeDescriptorManager.", function () {
                        expect(isBaseTypeIdentifierSpy).to.be.called;
                    });

                    it("first argument to isBaseTypeIdentifier should be same as 'typeIdentifier' on selectedData.", function () {
                        expect(isBaseTypeIdentifierSpy.secondCall.args[0]).to.be.equal(contentNodeType);
                    });

                    it("second argument to isBaseTypeIdentifier should be 'episerver.commerce.catalog.contenttypes.nodecontentbase'", function () {
                        expect(isBaseTypeIdentifierSpy.secondCall.args[1]).to.be.equal("episerver.commerce.catalog.contenttypes.nodecontentbase");
                    });

                    describe("when clipboard is empty", function () {
                        it("should keep 'canExecute' value of 'false'.", function () {
                            expect(command.get("canExecute")).to.be.equal(false);
                        });
                    });

                    describe("when clipboard has data", function () {
                        beforeEach(function () {
                            clipboard.set("data", [{ type: "epi.cms.contentdata", data: { typeIdentifier: contentNodeType } }]);
                        });

                        it("should set 'canExecute' to 'true'.", function () {
                            expect(command.get("canExecute")).to.be.equal(true);
                        });
                    });
                });

                describe("when copy a product then set selectionData with product content type", function () {
                    var productType = "episerver.commerce.sample.models.metadataclasses.mediaproductcontent";
                    beforeEach(function () {
                        command.typeDescriptorManager.isBaseTypeIdentifier = function (type, base) {
                            return base === ModelSupport.contentTypeIdentifier.productContent;
                        };

                        selection.set("data", [{ type: "epi.cms.contentdata", data: { typeIdentifier: productType } }]);
                        clipboard.set("copy", true);
                        command._onModelChange();
                    });
                });

                describe("when cut a product then set selectionData with product content type", function () {
                    var productType = "episerver.commerce.sample.models.metadataclasses.mediaproductcontent";
                    beforeEach(function () {
                        command.typeDescriptorManager.isBaseTypeIdentifier = function (type, base) {
                            return base === ModelSupport.contentTypeIdentifier.productContent;
                        };

                        selection.set("data", [{ type: "epi.cms.contentdata", data: { typeIdentifier: productType } }]);
                        clipboard.set("copy", false);
                        command._onModelChange();
                    });

                    it("should set 'canExecute' to 'false'.", function () {
                        expect(command.get("canExecute")).to.be.equal(false);
                    });
                });
            });
        });
    });

define([
    "dojo/_base/declare",
    "dojo/Stateful",
    "dojo/Deferred",
    "epi-ecf-ui/component/ContentContextMenuCommandProvider",
    "epi-ecf-ui/contentediting/ModelSupport",
    "epi/dependency",
    "epi/shell/store/Registry",
    "epi/shell/ClipboardManager",
    "epi/shell/selection",
    "epi-cms/component/command/ChangeContext",
    "epi-cms/command/NewContent",
    "epi-cms/command/DeleteContent",
    "epi-cms/command/CutContent",
    "epi-cms/command/CopyContent",
    "epi-cms/command/PasteContent",
    "epi-cms/command/TranslateContent",
    "epi-cms/content-approval/command/EditApprovalDefinition"
], function (
    declare,
    Stateful,
    Deferred,
    ContentContextMenuCommandProvider,
    ModelSupport,
    dependency,
    Registry,
    ClipboardManager,
    Selection,
    ChangeContextCommand,
    NewContent,
    DeleteCommand,
    CutCommand,
    CopyCommand,
    PasteCommand,
    TranslateCommand,
    EditApprovalDefinitionCommand
) {
    describe("epi-ecf-ui/component/ContentContextMenuCommandProvider", function () {

        var provider, contentRepositoryDescriptorsMock, registry, baseCommand;

        var constructDependencies = function () {
            registry = new Registry();
            registry.add("epi.commerce.global.clipboard", new ClipboardManager());
            dependency.register("epi.commerce.global", registry);

            registry = new Registry();
            registry.add("epi.cms.contenttype", {
                query: function () {
                    return [{ id: 22 }];
                },
                get: function (contentTypeID) {
                    return {
                        availableContentTypes: [
                            ModelSupport.contentTypeIdentifier.rootContent,
                            ModelSupport.contentTypeIdentifier.catalogContent,
                            ModelSupport.contentTypeIdentifier.nodeContent
                        ]
                    };
                }
            });
            registry.add("epi.cms.content.light", {});
            dependency.register("epi.storeregistry", registry);
            contentRepositoryDescriptorsMock = {
                catalog: {
                    containedTypes: ["episerver.commerce.product"],
                    creatableTypes: ["episerver.commerce.product", "episerver.commerce.node", "episerver.commerce.catalog"],
                    mainNavigationTypes: [],
                    roots: [3]
                },
                get: function() { return this.catalog; }
            };
            dependency.register("epi.shell.Profile", {});
            dependency.register("epi.cms.ApprovalService", {});
            dependency.register("epi.cms.contentRepositoryDescriptors", contentRepositoryDescriptorsMock);

            var clipboard = new ClipboardManager({});
            var selection = new Selection();
            baseCommand = {
                repositoryKey: "catalog",
                clipboard: clipboard,
                clipboardManager: clipboard,
                selection: selection
            };
        };

        beforeEach(function () {
            constructDependencies();
            var clipboard = new ClipboardManager({
            });
            provider = new ContentContextMenuCommandProvider(baseCommand);
        });

        afterEach(function () {
            dependency.clear();
            registry = null;
            provider = null;
        });

        describe('#updateCommandModel', function () {
            describe('When updating change context command', function () {
                beforeEach(function () {
                    provider.set("commands", [new ChangeContextCommand()]);
                });
                it('should be disabled for Root content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.rootContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be enabled for Catalog content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.catalogContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
                it('should be enabled for Node content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.nodeContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
                it('should be enabled for Product content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.productContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
            });

            describe('When updating add content command', function () {
                beforeEach(function () {
                    // create command is a container, which contains a list of NewContent command, for a specific type.
                    var createCommandClass = declare([Stateful], {
                        constructor: function (params) {
                            declare.safeMixin(this, params);
                        },

                        commands: [new NewContent(baseCommand)]
                    });
                    provider.set("commands", [new createCommandClass(baseCommand)]);
                });
                it('should be disabled for Root content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.rootContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should not change isAvailable for Catalog content', function () {
                    var isAvailable = provider.get("commands")[0].get("isAvailable");
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.catalogContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(isAvailable);
                });
                it('should not change isAvailable for Node content', function () {
                    var isAvailable = provider.get("commands")[0].get("isAvailable");
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.nodeContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(isAvailable);
                });
                it('should not change isAvailable for Product content', function () {
                    var isAvailable = provider.get("commands")[0].get("isAvailable");
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.productContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(isAvailable);
                });
            });

            describe('When updating edit content command', function () {
                beforeEach(function () {
                    var editCommand = new ChangeContextCommand({
                        category: "context",
                        forceContextChange: true,
                        viewName: "formedit"
                    });
                    editCommand.set("_handles", []);
                    provider.set("commands", [editCommand]);
                });
                it('should be disabled for Root content', function () {
                    provider.updateCommandModel({
                    typeIdentifier: ModelSupport.contentTypeIdentifier.rootContent,
                    watch: function() {}
                    });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be enabled for Catalog content', function () {
                    provider.updateCommandModel({
                    typeIdentifier: ModelSupport.contentTypeIdentifier.catalogContent,
                    watch: function() {}
                    });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
                it('should be enabled for Node content', function () {
                    provider.updateCommandModel({
                    typeIdentifier: ModelSupport.contentTypeIdentifier.nodeContent,
                    watch: function() {}
                    });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
                it('should be enabled for Product content', function () {
                    provider.updateCommandModel({
                    typeIdentifier: ModelSupport.contentTypeIdentifier.productContent,
                    watch: function() {}
                    });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
            });

            describe('When updating cut content command', function () {
                beforeEach(function () {
                    provider.set("commands", [new CutCommand(baseCommand)]);
                });
                it('should be disabled for Root content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.rootContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be disabled for Catalog content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.catalogContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be enabled for Node content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.nodeContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
                it('should be enabled for Product content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.productContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
            });

            describe('When updating copy content command', function () {
                beforeEach(function () {
                    provider.set("commands", [new CopyCommand(baseCommand)]);
                });
                it('should be disabled for Root content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.rootContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be disabled for Catalog content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.catalogContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be enabled for Node content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.nodeContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
                it('should be enabled for Product content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.productContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
            });

            describe('When updating delete content command', function () {
                beforeEach(function () {
                    provider.set("commands", [new DeleteCommand(baseCommand)]);
                });
                it('should be disabled for Root content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.rootContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be enabled for Catalog content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.catalogContent, name: 'test' });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
                it('should be enabled for Node content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.nodeContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
                it('should be enabled for Product content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.productContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
            });

            describe('When updating paste content command', function () {
                beforeEach(function () {
                    provider.set("commands", [new PasteCommand(baseCommand)]);
                });
                it('should be disabled for Root content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.rootContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be enabled for Catalog content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.catalogContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
                it('should be enabled for Node content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.nodeContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
                it('should be enabled for Product content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.productContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
            });

            describe('When updating translate content command', function () {
                beforeEach(function () {
                    provider.set("commands", [new TranslateCommand(baseCommand)]);
                });
                it('should be disabled for Root content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.rootContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be disabled for Catalog content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.catalogContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be disabled for Node content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.nodeContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be disabled for Product content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.productContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
            });

            describe('When updating command with null model', function () {
                beforeEach(function () {
                    provider.set("commands", [new TranslateCommand(baseCommand)]);
                });
                it('should be disabled for Root content', function () {
                    provider.updateCommandModel(null);
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be disabled for Catalog content', function () {
                    provider.updateCommandModel(null);
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be disabled for Node content', function () {
                    provider.updateCommandModel(null);
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be disabled for Product content', function () {
                    provider.updateCommandModel(null);
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
            });

            describe('When updating add catalog content command', function () {
                beforeEach(function () {
                    provider.set("commands", [new NewContent(baseCommand)]);
                });
                it('should be enabled for Root content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.rootContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
                it('should be disabled for Catalog content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.catalogContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be disabled for Node content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.nodeContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
                it('should be disabled for Product content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.productContent });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(false);
                });
            });

            describe('When updating edit approval definition command', function () {
                beforeEach(function () {
                    var profileMock = {
                        get: sinon.stub().returns(new Deferred().resolve())
                    };

                    provider.set("commands", [new EditApprovalDefinitionCommand({
                        profile: profileMock
                    })]);
                });
                it('should be enabled for Root content', function () {
                    provider.updateCommandModel({ typeIdentifier: ModelSupport.contentTypeIdentifier.rootContent, capabilities: {} });
                    expect(provider.get("commands")[0].get("isAvailable")).to.be.equal(true);
                });
            });
        });
    });
});

define([
    "dijit/_WidgetBase",
    "epi/shell/form/formFieldRegistry",
    "epi-ecf-ui/contentediting/PromotionContainer"
],

function (
    _WidgetBase,
    formFieldRegistry,
    PromotionContainer
    ) {

    describe("epi-ecf-ui/contentediting/PromotionContainer", function () {
        var subject;
        beforeEach(function () {
            subject = new PromotionContainer({ contentType: {} });
        });
        describe("#_injectSpan", function () {
            describe("when text is null", function () {
                it("should return null", function () {
                    var result = subject._injectSpan(null);
                    expect(result).to.equal(null);
                });
            });
            describe("when text has no segment", function () {
                it("should return same text", function () {
                    var text = "Text without segment";
                    var result = subject._injectSpan(text);
                    expect(result).to.equal(text);
                });
            });
            describe("when text has one segment", function () {
                it("should replace segment with span and set css class", function () {
                    var result = subject._injectSpan("Text with {one}one segment{/}");
                    expect(result).to.equal('Text with <span class="epi-promotion-link epi-promotion--one">one segment</span>');
                });
            });
            describe("when text has multiple segments", function () {
                it("should replace all segments", function () {
                    var result = subject._injectSpan("{text}Text{/} {multiple}with multiple{/} {segments}different segments{/}");
                    expect(result).to.equal('<span class="epi-promotion-link epi-promotion--text">Text</span> <span class="epi-promotion-link epi-promotion--multiple">with multiple</span> <span class="epi-promotion-link epi-promotion--segments">different segments</span>');
                });
            });
        });
        describe('#addChild', function () {
            var child, wrapper;

            beforeEach(function () {
                child = new _WidgetBase({
                    promotionRegion: null,
                    _type: formFieldRegistry.type.field,
                    _hint: "promotion"
                });
            });

            describe('when promotion region exist on child', function () {
                beforeEach(function () {
                    child.promotionRegion = "region";
                    subject.addChild(child);
                    wrapper = subject.getChildren()[0];
                });

                it('should add promotion-region class', function () {
                    expect(wrapper.domNode.className).to.contain("epi-promotion-block");
                });

                it('should add promotion-type-x class, where x is the promotionRegion variable', function () {
                    expect(wrapper.domNode.className).to.contain("epi-promotion--" + child.promotionRegion);
                });
            });

            describe('when promotion region doesnt exist on child', function () {
                beforeEach(function () {
                    subject.addChild(child);
                    wrapper = subject.getChildren()[0];
                });

                it('should not add promotion-type- class', function () {
                    expect(wrapper.domNode.className).to.not.contain("epi-promotion-");
                });
            });

            describe('when the child is a parent (block)', function () {
                beforeEach(function () {
                    child._type = formFieldRegistry.type.parent;
                });

