                this.id = item.id;
                this.assetKey = item.assetKey;
                this.groupName = item.groupName;

                // Simulate asynchronous item initialization
                setTimeout(function () {
                    self.initialized = true;
                    deferred.resolve();
                }, 1);

                return deferred.promise;
            }
        };

        var syncItemModelType = function () { };
        syncItemModelType.prototype = {
