                label: "Name"
            },
            groupName: { label: "groupName" }
        };

        function getAllItemCommandsHelper(model, itemIndex, commandMask) {
            /*jslint bitwise: true*/
            return model.getItemCommands(
                model.get("items")[itemIndex],
                commandMask ||
                (CollectionEditorModel.commandMask.edit |
                 CollectionEditorModel.commandMask.remove |
                 CollectionEditorModel.commandMask.moveUp |
                 CollectionEditorModel.commandMask.moveDown)
            );
        }

        function getItemCommandByNameHelper(model, itemIndex, commandName) {
            var commands = getAllItemCommandsHelper(model, itemIndex);
            return commands.filter(function (cmd) { return cmd.name === commandName; })[0];
        }

        // ---------------------------------------------------------------------------

        beforeEach(function () {
            /*jslint bitwise: true*/
            model = new CommerceMediaCollectionEditorModel({
                itemType: testItemType,
                metadataManager: fakeMetadataManager,
