                                    CollectionEditorModel.commandMask.remove |
                                    CollectionEditorModel.commandMask.moveUp |
                                    CollectionEditorModel.commandMask.moveDown)

            });
        });

        describe("#addItem", function () {
            describe("when grid item is empty", function () {
                var item;
                beforeEach(function (done) {
                    model.data = [];
                    model.itemModelType = asyncItemModelType;
                    model.initialize().then(function () {
                        item = { id: 21, foo: "bar" };
                        done();
                    });
                });

                it("should put new item to empty list", function (done) {
                    model.addItem(item).then(function () {
                        expect(model.get("items")[0].id).to.equal(21);
                        expect(model.get("items").length).to.equal(1);
                        done();
                    });
                });
            });

            describe("when grid item is not empty", function () {
                var item, refItem;
                beforeEach(function (done) {
                    model.data = [{ id: 1, assetKey: "1" }, { id: 2, assetKey: "2" }, { id: 3, assetKey: "3" }];
                    model.itemModelType = asyncItemModelType;
