                    });
                });

                it("should put new item to the very top", function (done) {
                    model.addItem(item, refItem, true).then(function () {
                        expect(model.get("items")[0].id).to.equal(21);
                        expect(model.get("items").length).to.equal(4);
                        done();
                    });
                });

                describe("when adding item is existed in item list", function () {
                    it("should move new item to the very top", function (done) {
                        model.addItem({ id: 3, assetKey: "3" }, refItem, true).then(function () {
                            expect(model.get("items")[0].id).to.equal(3);
                            expect(model.get("items").length).to.equal(3);
                            done();
                        });
                    });
                });
                describe('and the item does not have any groupName', function () {
                    it('should add the defaultGroupName', function (done) {
                        var defaultValue = "my default value";
                        model.defaultAssetGroupName = defaultValue;
                        model.addItem({ id: 4, assetKey: "new" }, refItem, true).then(function () {
                            expect(model.get("items")[0].groupName).to.equal(defaultValue);
                            done();
                        });
                    });
                });
                describe('and the item has a groupName', function () {
                    it('should not override the groupName with the default value', function (done) {
                        var myDefinedValue = "defined value";
                        model.defaultAssetGroupName = "my default value";
                        model.addItem({ id: 4, assetKey: "new", groupName: myDefinedValue }, refItem, true).then(function () {
                            expect(model.get("items")[0].groupName).to.equal(myDefinedValue);
                            done();
                        });
                    });
                });
            });
        });

        describe("#saveItem", function () {
            var item = { id: 1, foo: "bar", assetKey: "1" };
            var spy;
            beforeEach(function (done) {
                model.data = [item, { id: 2, assetKey: "2" }, { id: 3, assetKey: "3" }];
                model.itemModelType = syncItemModelType;
                model.initialize().then(function () {
                    done();
                });
                spy = sinon.spy(model._itemModels, "splice");
            });

            afterEach(function () {
                spy.restore();
            });

            describe("when other item has same key", function () {
                it("should not save the item", function () {
                    var itemIndex = model.data.indexOf(item);
