                    expect(spy).to.have.not.been.called;
                });
            });

            describe("when no other item has same key", function () {
                it("should save the item", function () {
                    var itemIndex = model.data.indexOf(item);
                    model.saveItem(item, itemIndex);
                    expect(spy).to.have.been.called.once;
                });
            });
        });

        describe("#generateFormatters", function () {
            describe("when column definitions has a thumbnail", function () {

                it("display name with its thumnail", function () {
                    model.generateFormatters(fakeColumnDefinitionsHasThumbnail);
                    expect(fakeColumnDefinitionsHasThumbnail.thumbnailUrl.formatter).to.exist;
