define([
    "dojo/_base/array",
    "dojo/_base/declare",
    "dojo/_base/lang",
    "dojo/Deferred",
    "dojo/json",
    "dojo/Stateful",
    "dojo/store/Memory",
    "epi/shell/store/Patchable",
    "epi/shell/TypeDescriptorManager",
    "epi-cms/store/CustomQueryEngine",
    "epi-ecf-ui/store/Aggressive",
    "epi-ecf-ui/MarketingUtils",
    "dojo/text!/mocks/data/PromotionData.json"
], function (
    array,
    declare,
    lang,
    Deferred,
    json,
    Stateful,
    MemoryStore,
    Patchable,
    TypeDescriptorManager,
    CustomQueryEngine,
    Aggressive,
    MarketingUtils,
    promotionData
) {
    return declare([Stateful], {
        contentData: null,
        masterStore: null,
        aggressiveStore: null,

        constructor: function () {
            TypeDescriptorManager.registerTypeSettings([{
                typeIdentifier: MarketingUtils.contentTypeIdentifier.salesCampaign,
                dndTypes: MarketingUtils.contentTypeIdentifier.salesCampaign,
                iconClass: "epi-iconObjectCampaign"
            }, {
                typeIdentifier: MarketingUtils.contentTypeIdentifier.promotionData,
                dndTypes: MarketingUtils.contentTypeIdentifier.promotionData,
                iconClass: "epi-iconObjectItem"
            }]);

            var contentData = json.parse(promotionData);
            var masterStore = new MemoryStore({ data: contentData, idProperty: "contentLink", queryEngine: CustomQueryEngine });
            masterStore._queryBackup = masterStore.query;
            masterStore.query = function (query, options) {
                var results;
                if (query && query.query === "getchildren") {
                    results = array.filter(this.data, function (cat) {
                        return cat.parentLink === query.referenceId;
                    });
                }
                if (query && query.query === "getdescendents") {
                    results = array.filter(this.data, function (cat) {
                        return (query.includeRoot && cat.contentLink === query.referenceId) || query.typeIdentifiers.indexOf(cat.typeIdentifier) > -1;
                    });
                }

                if (results) {
                    var df = new Deferred();
                    setTimeout(function(){
                        df.resolve(results);
                    }, 50);

                    var promise = df.promise;
                    promise = lang.delegate(promise, {
                        forEach: results.forEach
                    });
                    return promise;
                } else {
                    return this._queryBackup(query, options);
                }
            };

            var options = {
                rootId: 1,
                typeIdentifiers: [
                    MarketingUtils.contentTypeIdentifier.campaignFolder,
                    MarketingUtils.contentTypeIdentifier.salesCampaign,
                    MarketingUtils.contentTypeIdentifier.promotionData
                ],
                includeRoot: true
            };
            masterStore = Patchable(masterStore, new MemoryStore({ idProperty: "contentLink" }));
            var aggressiveStore = Aggressive(masterStore, options);

            this.set("contentData", contentData);
            this.set("masterStore", masterStore);
            this.set("aggressiveStore", aggressiveStore);
        }
    });
});

