                        expect(catalogContentList.model.moveItem.secondCall).to.be.calledWith({ id: 2, properties: { isLinkedToCurrentCategory: true } });
                        expect(catalogContentList.model.moveItem.thirdCall).to.be.calledWith({ id: 1, properties: { isLinkedToCurrentCategory: true } });
