    "epi-cms/contentediting/ContentActionSupport",
    // commerce
    "epi-ecf-ui/widget/viewmodel/CatalogContentListViewModel"
], function (declare, Stateful, dependency, Registry, ClipboardManager, CreateCommandsMixin, ContentActionSupport, CatalogContentListViewModel) {

    describe("epi-ecf-ui/widget/viewmodel/CatalogContentListViewModel", function () {

        var viewmodel, store, registry;
        var singleObjectModel;
        var contentRepositoryDescriptorsMock;

        var constructDependencies = function () {
            contentRepositoryDescriptorsMock = {
                product: {
                    containedTypes: ["episerver.commerce.product"],
                    creatableTypes: ["episerver.commerce.product"],
                    mainNavigationTypes: [],
                    roots: [3]
                },
                get: function (key) { return this[key]; }

            };
            store = {
                get: function () {
                    return {
                        typeIdentifier: "episerver.core.contentdata",
                        availableContentTypes: [1, 2]
                    };
                },
                query: function () {
                    return {
                        observe: function () { }
                    };
                }
            };
        };

        beforeEach(function () {
            constructDependencies();
            registry = new Registry();
            registry.add("epi.commerce.global.clipboard", new ClipboardManager());
            dependency.register("epi.commerce.global", registry);
            dependency.register("epi.cms.contentRepositoryDescriptors", contentRepositoryDescriptorsMock);
            dependency.register("epi.cms.ContentHierarchyService", {});

            registry = {
                get: function (storeKey) {
                    if (storeKey === "epi.commerce.relation") {
                        return {
                            query: function () {
                                return [
                                    { source: "1", sortOrder: 1 },
                                    { source: "2", sortOrder: 2 },
                                    { source: "3", sortOrder: 3 }
                                ];
                            },
                            get: function () {
                                return {};
                            }
                        };
                    } else {
                        return store;
                    }
                }
            };
            dependency.register("epi.storeregistry", registry);
            viewmodel = new CatalogContentListViewModel({
                res: {
                    detachconfirmation: {},
                    deleteconfirmation: {},
                    sorting: {
                        contextmenu: {}
                    }
                },
                creatableTypes: ["episerver.commerce.product"]
            });

            /*jslint bitwise: true*/
            singleObjectModel = {
                accessMask: ContentActionSupport.accessLevel.FullAccess,
                isDeleted: false,
                isWastebasket: false,
                providerCapabilityMask: ContentActionSupport.providerCapabilities.Create | ContentActionSupport.providerCapabilities.Edit,
                contentTypeID: 1,
                hasTranslationAccess: true,
                isPreferredLanguageAvailable: true,
                contentLink: 27,
                properties: {}
            };
        });

        afterEach(function () {
            dependency.clear();
            contentRepositoryDescriptorsMock = null;
            viewmodel.destroy();
            viewmodel = null;
            store = null;
            registry = null;
        });

        describe("model setter", function () {
            describe("if model is null", function () {
                it("edit command should not be executable", function () {
                    viewmodel.set("model", null);
                    expect(viewmodel._commandRegistry.edit.command.canExecute).to.be.equal(false);
                });
                it("create product command should not be executable", function () {
                    viewmodel.set("model", null);
                    expect(viewmodel._commandRegistry["episerver.commerce.product"].command.canExecute).to.be.equal(false);
                });
                it("selection should be empty", function () {
                    viewmodel.set("model", null);
                    expect(viewmodel.selection.get("data")).to.be.empty;
                });
            });

            describe("if model is a single object", function () {
                it("edit command should be executable", function () {
                    viewmodel.set("model", singleObjectModel);
                    expect(viewmodel._commandRegistry.edit.command.canExecute).to.be.equal(true);
                });
                it("edit command's model should be input model", function () {
                    viewmodel.set("model", singleObjectModel);
                    expect(singleObjectModel, viewmodel._commandRegistry.edit.command.get("model")).to.be.equal;
                });
                it("create command should be executable", function () {
                    viewmodel.set("model", singleObjectModel);
                    expect(viewmodel._commandRegistry["episerver.commerce.product"].command.canExecute).to.be.equal(true);
                });
                it("create command's model should be input model", function () {
                    viewmodel.set("model", singleObjectModel);
                    expect(singleObjectModel, viewmodel._commandRegistry["episerver.commerce.product"].command.get("model")).to.be.equal;
                });
                it("selection should be empty", function () {
                    viewmodel.set("model", singleObjectModel);
                    expect(viewmodel.selection.get("data")).to.be.empty;
                });
            });

            describe("if model is an one-item-array", function () {
                it("edit command should be executable", function () {
                    viewmodel.set("model", { data: [singleObjectModel] });
                    expect(viewmodel._commandRegistry.edit.command.canExecute).to.be.equal(true);
                });
                it("edit command's model should be input model", function () {
                    viewmodel.set("model", { data: [singleObjectModel] });
                    expect(singleObjectModel, viewmodel._commandRegistry.edit.command.get("model")).to.be.equal;
                });
                it("create command should be executable", function () {
                    viewmodel.set("model", { data: [singleObjectModel] });
                    expect(viewmodel._commandRegistry["episerver.commerce.product"].command.canExecute).to.be.equal(true);
                });
                it("create command's model should be input model", function () {
                    viewmodel.set("model", { data: [singleObjectModel] });
                    expect(singleObjectModel, viewmodel._commandRegistry["episerver.commerce.product"].command.get("model")).to.be.equal;
                });
                it("selection should not be empty", function () {
                    viewmodel.set("model", { data: [singleObjectModel] });
                    expect(viewmodel.selection.get("data")).to.be.not.empty;
                });
            });

            describe("if model is a multiple-item-array", function () {
                it("edit command should not be executable", function () {
                    viewmodel.set("model", { data: [singleObjectModel, singleObjectModel] });
                    expect(viewmodel._commandRegistry.edit.command.canExecute).to.be.equal(false);
                });
                it("create product command should not be executable", function () {
                    viewmodel.set("model", { data: [singleObjectModel, singleObjectModel] });
                    expect(viewmodel._commandRegistry["episerver.commerce.product"].command.canExecute).to.be.equal(false);
                });
                it("selection should not be empty", function () {
                    viewmodel.set("model", { data: [singleObjectModel, singleObjectModel] });
                    expect(viewmodel.selection.get("data")).to.be.not.empty;
                });
            });

            describe("if model have context id with work id", function () {
                it("set selection data with context id without work id", function () {
                    viewmodel.set("model", { data: [singleObjectModel], contextId: "27_1_CatalogContent" });
                    expect(viewmodel.selection.get("data")[0].contextId).to.be.equal("27__CatalogContent");
                });
            });

            describe('when setting sort mode', function () {
                beforeEach(function () {
                    viewmodel.creatableTypes = ["popupCommand"];
                    viewmodel._setupCommands();
                });
                describe('to true', function () {
                    it('should update command availability', function () {
                        viewmodel.set("sortMode", true);
                        expect(viewmodel._commandRegistry.copy.command.canExecute).to.equal(false);
                        expect(viewmodel._commandRegistry.cut.command.canExecute).to.equal(false);
                        expect(viewmodel._commandRegistry.paste.command.canExecute).to.equal(false);
                        expect(viewmodel._commandRegistry.pasteOnContext.command.canExecute).to.equal(false);
                        expect(viewmodel._commandRegistry.detach.command.canExecute).to.equal(false);
                        expect(viewmodel._commandRegistry.remove.command.canExecute).to.equal(false);
                        expect(viewmodel._commandRegistry.moveup.command.isAvailable).to.equal(true);
                        expect(viewmodel._commandRegistry.movedown.command.isAvailable).to.equal(true);
                    });
                });
                describe('to false', function () {
                    var originalValue = true;

                    beforeEach(function () {
                        viewmodel._commandRegistry.pasteOnContext.command.set("canExecute", originalValue);

                        viewmodel.commands.forEach(function (command, index) {
                            command.set("canExecute", originalValue);
                        });

                        viewmodel.set("sortMode", false);
                    });

                    it('should use the default availability of the command', function () {
                        expect(viewmodel._commandRegistry.copy.command.canExecute).to.equal(originalValue);
                        expect(viewmodel._commandRegistry.cut.command.canExecute).to.equal(originalValue);
                        expect(viewmodel._commandRegistry.paste.command.canExecute).to.equal(originalValue);
                        expect(viewmodel._commandRegistry.pasteOnContext.command.canExecute).to.equal(originalValue);
                        expect(viewmodel._commandRegistry.detach.command.canExecute).to.equal(originalValue);
                        expect(viewmodel._commandRegistry.remove.command.canExecute).to.equal(originalValue);
                    });

                    it('sort commands should be disabled', function () {
                        expect(viewmodel._commandRegistry.moveup.command.isAvailable).to.equal(false);
                        expect(viewmodel._commandRegistry.moveup.command.canExecute).to.equal(false);
                        expect(viewmodel._commandRegistry.movedown.command.isAvailable).to.equal(false);
                        expect(viewmodel._commandRegistry.movedown.command.canExecute).to.equal(false);
                    });
                });

            });
        });

        describe("#moveItem", function () {
            beforeEach(function () {
                viewmodel.treeStoreModel.relationStore.put = sinon.spy();
                viewmodel.model = {};
            });
            describe("when moving before target", function () {
                it("should save relation with same sort order as the drop target", function () {
                    viewmodel.moveItem({ contentLink: "1" }, { contentLink: "2" }, true);
                    expect(viewmodel.treeStoreModel.relationStore.put).to.be.calledWith({ source: "1", sortOrder: 2 });
                });
            });
            describe("when moving after target", function () {
                it("should save relation with sort order one higher than drop target", function () {
                    viewmodel.moveItem({ contentLink: "1" }, { contentLink: "2" }, false);
                    expect(viewmodel.treeStoreModel.relationStore.put).to.be.calledWith({ source: "1", sortOrder: 3 });
                });
            });
            describe("when moving after whole list", function () {
                it("should save relation with sort order one higher than last target", function () {
                    viewmodel.moveItem({ contentLink: "1" }, null, false);
                    expect(viewmodel.treeStoreModel.relationStore.put).to.be.calledWith({ source: "1", sortOrder: 4 });
                });
            });
        });
    });
});

