        },

        _setContentTypeNameAttr: { node: "contentTypeNameNode", type: "innerHTML" },

        _setContentTypeDescriptionAttr: { node: "contentTypeDescriptionNode", type: "innerHTML" },

        _injectSpan: function (text) {
