    "epi/i18n!epi/cms/nls/commerce.widget.pricecollection.message"
],
function (
    //dojo
    array,
    declare,
    all,

    // dojox
    htmlEntities,

    // epi
    DelegateCommand,
    epiDate,
    formatters,

    // commerce
    _OverviewEditorModelBase,

    // resources
    resources,
    res
) {
    return declare([_OverviewEditorModelBase], {
        // module:
        //      epi-ecf-ui/contentediting/viewmodel/InventoryOverviewEditorModel
        // summary:
        //      Represents the model for InventoryOverviewEditor

        _storeKey: "epi.commerce.inventory",

        _createQueryOptions: function () {
            // summary:
            //      Creates and returns query options, based on selected content link, market id and customer group (if any).
            // tags:
            //      private

            return {
                query: { referenceId: this.get("contentLink"), query: "getinventory" }
            };
        },

        getCommands: function (model, category) {
            return [
                new DelegateCommand({
                    name: "remove",
                    label: resources.commands.remove,
                    iconClass: "epi-iconClose",
                    category: category,
                    model: model,
                    canExecute: true,
                    isAvailable: true,
                    delegate: function (cmd) {
                        this.emit("removeCommandEvent");
                    }.bind(this)
                })
            ];
        },

        removeItems: function (models) {
            var promises = [];

            for (var i = 0, j = models.length; i < j; i += 1) {
                promises.push(this.store.remove(models[i].contentLink + "$" + models[i].warehouseCode));
            }

            return all(promises).then(function (removed) {
                this.emit("itemsRemoved", { removedItems: removed });
            }.bind(this));
        },

        generateFormatters: function (columnDefinitions, editableColumns, metadata) {
            for (var definitionName in columnDefinitions) {
                var column = columnDefinitions[definitionName];

                if (definitionName === "warehouseCode") {
                    column.formatter = this._getWarehouseFormatter(metadata);
                    continue;
                }

                if (!this._isColumnEditable(definitionName, editableColumns)) {
                    continue;
                }

                if (definitionName === "backorderAvailableUtc" || definitionName === "preorderAvailableUtc" || definitionName === "purchaseAvailableUtc") {
                    column.formatter = formatters.localizedDate;
                } else if (definitionName === "isTracked") {
                    column.formatter = formatters.friendlyBoolean;
                } else if (column.editorArgs && column.editorArgs.selections && column.editorArgs.selections.length > 0) {
                    column.formatter = this._getSelectionFormatter(column);
                } else {
                    column.formatter = this._editableValueFormatter;
                }

                column.className = column.className ? column.className + " epi-dgrid--editable" : "epi-dgrid--editable";
            }
        },

        getAllItems: function (contentLink) {
            return this.store.query({ referenceId: contentLink });
        },

        _getWarehouseFormatter: function (metadata) {
            // summary:
            //      A custom formatter for the warehouse code. Display the selection for the current value, but don't make the field editable.

            var selections;
            array.some(metadata.properties, function (property) {
                if (property.name === "WarehouseCode") {
                    selections = property.selections;
                    return true;
                }
            });

            return function (value) {
                array.some(selections, function (selection) {
                    if (selection.value === value) {
                        value = selection.text;
                        return true;
                    }
                });
                return value;
            }.bind(this);
        }
    });
});

define([
    // dojo
    "dojo/_base/declare",
    "dojo/_base/lang",
    "dojo/currency",
    "dojo/promise/all",

    // dojox
    "dojox/html/entities",

    // epi
    "epi/shell/command/DelegateCommand",
    "epi/datetime",

    // commerce
    "./_OverviewEditorModelBase",

    // resources
    "epi/i18n!epi/cms/nls/commerce.widget.pricingoverview.grid",
    "epi/i18n!epi/cms/nls/commerce.widget.pricecollection.message"
],
function (
    //dojo
    declare,
    lang,
    currency,
    all,

    // dojox
    htmlEntities,

    // epi
    DelegateCommand,
    epiDate,

    // commerce
    _OverviewEditorModelBase,

    // resources
    resources,
    res
) {
    return declare([_OverviewEditorModelBase], {
        // module:
        //      epi-ecf-ui/contentediting/viewmodel/PricingOverviewEditorModel
        // summary:
        //      Represents the model for PricingOverviewEditor

        _storeKey: "epi.commerce.price",

        _createQueryOptions: function () {
            // summary:
            //      Creates and returns query options, based on selected content link, market id and customer group (if any).
            // tags:
            //      private

            var query = { referenceId : this.get("contentLink"), query: "getprice" },
                ignoreOptions = ["query", "referenceId"];

            if (this.get("marketId")) {
                lang.mixin(query, { marketId: this.get("marketId") });
                if (query.marketId === "ALL") {
                    ignoreOptions.push("marketId");
                }
            }

            if (this.get("priceCode")) {
                lang.mixin(query, { priceCode: this.get("priceCode") });
                if (query.priceCode === "ALL"){
                    ignoreOptions.push("priceCode");
                }
            }

            return { query: query, options: { ignore: ignoreOptions } };
        },

        getCommands: function (model, category) {
            var commonSettings = {
                category: category,
                model: model,
                canExecute: true,
                isAvailable: true
            };

            return [
                new DelegateCommand(lang.mixin({
                    name: "duplicate",
                    label: resources.commands.duplicate,
                    iconClass: "epi-iconDuplicatePage",
                    delegate: function (cmd) {
                        this.emit("duplicateCommandEvent");
                    }.bind(this)
                }, commonSettings)),
                new DelegateCommand(lang.mixin({
                    name: "remove",
                    label: resources.commands.remove,
                    iconClass: "epi-iconClose",
                    delegate: function (cmd) {
                        this.emit("removeCommandEvent");
                    }.bind(this)
                }, commonSettings))
            ];
        },

        removeItems: function (models) {
            var promises = [];

            for (var i = 0, j = models.length; i < j; i += 1) {
                promises.push(this.store.remove(models[i].id));
            }

            return all(promises).then(function(removed) {
                this.emit("itemsRemoved", { removedItems: removed });
            }.bind(this));
        },

        generateFormatters: function (columnDefinitions, editableColumns) {
            for(var definitionName in columnDefinitions){
                if (!this._isColumnEditable(definitionName, editableColumns)){
                    continue;
                }
                var column = columnDefinitions[definitionName];
                if (definitionName === "validDate"){
                    column.formatter = this._validDateFormatter.bind(this);
                } else if(definitionName === "unitPrice"){
                    column.formatter = this._unitPriceFormatter.bind(this);
                } else if (definitionName === "priceCode"){
                    column.formatter = this._priceCodeFormatter.bind(this);
                } else if(column.editorArgs && column.editorArgs.selections && column.editorArgs.selections.length > 0){
                    column.formatter = this._getSelectionFormatter(column);
                } else{
                    column.formatter = this._editableValueFormatter;
                }
                column.className = column.className ? column.className + " epi-dgrid--editable" : "epi-dgrid--editable";
            }
        },

        _validDateFormatter: function(value){
            var formattedDate = this._formatValidDate(value);
            return this._editableValueFormatter(formattedDate);
        },

        _formatValidDate: function (value) {
            // Format datetime
            return this._formatDate(value.validFrom) + " - " + this._formatDate(value.validUntil);
        },

        _formatDate: function (datetime) {
            // summary:
            //      Format datetime to short type.
            // datetime: Object
            //      The raw data.
            // tags:
            //      public

            return datetime && datetime !== "" ? epiDate.toUserFriendlyString(datetime) : res.untildatenotdefined;
        },

        _unitPriceFormatter: function(value){
            var formattedCurrency = currency.format(value.amount, { currency: value.currency });
            return this._editableValueFormatter(formattedCurrency);
        },

        _priceCodeFormatter: function(value){
            var encodedValue = htmlEntities.encode(value);
            return this._editableValueFormatter(encodedValue);
        }
    });
});

define([
    // dojo
    "dojo/_base/declare",
    "dojo/_base/lang",
    "dojo/when",

    "epi/dependency",

    // epi cms
    "epi-cms/dgrid/formatters",
    "epi-cms/contentediting/editors/model/CollectionEditorModel",
    "epi-cms/_ContentContextMixin"
],
    function (
        //dojo
        declare,
        lang,
        when,

        dependency,

        // epi cms
        formatters,
        CollectionEditorModel,
        _ContentContextMixin
    ) {
        return declare([CollectionEditorModel, _ContentContextMixin], {
            // module: 
            //      epi-ecf-ui/contentediting/viewmodel/ReadOnlyCollectionEditorModel
            // summary:
            //      Represents the model for ReadOnly CollectionEditor

            // _storeKey: String
            //      Represents the key to get the store object. Implementation will set this property, to get, for example, price store, or inventory store
            _storeKey: null,

            // _store: Object
            //      Represents the REST store, to get data from.
            _store: null,

            // _contentLink: ContentReference
            //      Represents the underlay data, a content reference.
            _contentLink: null,

            // contentContext:
            //  accessMask: int
            //  isMasterLanguage: Boolean
            //      Represents if the current content link is in master language or not
            contentContext: null,

            // summary:
            //      Sets whether the editor's collection can be sorted or not.
            // value: Boolean
            //      The value true makes the collection sortable by clicking the grid's column headers, else false.
            // tags: 
            //      protected
            _enableSorting: false,

            postscript: function () {
                // summary:
                //      Post script initialization.
                // description:
                //      Set default values.
	    var registry = dependency.resolve("epi.storeregistry");
                this._store = this._store || registry.get(this._storeKey);
                this.inherited(arguments);
            },

            _preprocessResult: function (result) {
                // tags: 
                //      protected

                return result.models || result;
            },

            _dataSetter: function (value) {
                // summary:
                //      Set data property.
                // value: ContentReference
                //      Model value, which would be a ContentReference.
                // tags: 
                //      private

                // The model value is ContentReference. We need to query to the store, to get the appropriate data (for example: price list, or inventory list)
                this._contentLink = value;
                if (!value) {
                    this.data = value;
                    this._initData();

                    return;
                }

                when(this._store.query(this.createQuery(value)))
                    .then(this._preprocessResult.bind(this))
                    .then(function (processedResult) {
                        this.data = processedResult;
                        this._initData();
                    }.bind(this));

                when(this.getCurrentContent(), function (content) {
                    this.set("contentContext", {
                        accessMask: content.accessMask,
                        isMasterLanguage: content.currentLanguageBranch.isMasterLanguage
                    });
                }.bind(this));
            },

            _dataGetter: function () {
                // summary:
                //      Get data property.
                // tags: 
                //      private

                return this._contentLink;
            },

            createQuery: function (referenceId) {
                return { referenceId: referenceId };
            },

            generateFormatters: function (columnDefinitions) {
                // summary:
                //      Generate formatters for the specified column definitions.
                // columnDefinitions:
                //      The definition for the columns that should get the generated formatters.
                // tags: 
                //      override

                this.inherited(arguments);

                var typeColumn = columnDefinitions.contentTypeIdentifier;

                if (typeColumn) {
                typeColumn.formatter = function(typeIdentifier, additionalClass) {
                    return formatters.contentIcon(typeIdentifier, additionalClass);
                };
                }
                // Disable sort order in dgrid
                for (var columnName in columnDefinitions) {
                    lang.setObject("sortable", this._enableSorting, columnDefinitions[columnName]);
                }

                return columnDefinitions;
            }
        });
    });

define([
