                            settings: {
                                showLabel: false,
                                "class": "epi-chromeless",
                                onKeyDown: function (e) {
                                    if (e.keyCode === keys.ENTER) {
                                        //when pressing ENTER on button
                                        //we need to stop the grids key
                                        //handler changing context.
                                        event.stop(e);
                                    }
                                }
                            }
                        });
